        let proof = hex!("");
        let value = hex!("");
        chained_membership(
            &proof,
            &root,
            &value,
            &["acc", unsafe {
                &String::from_utf8_unchecked(
                    hex!("014152090B0C95C948EDC407995560FEED4A9DF81E").to_vec(),
                )
            }],
        )
        .unwrap();
        // let path: Vec<Vec<u8>> = vec![
